# s = ""
s="abcccccdd"
dic={}
#to form the longest palindromic string we need even number of characters
for i in s:
    if i not in dic:
        dic[i]=0
    dic[i]+=1
count=0
print(dic)
flag=False
for key,val in dic.items():
    # print(val)
    if val%2!=0:
        flag=True
    count+=val//2*2

if flag:
    print("yes")
    count+=1

print(count)